        }

        if (CustTableChangeProposalFieldEnablement::isEnabledFor(CustTableChangeProposalField::MethodOfPayment)
                && this.PaymentMethod != this.orig().PaymentMethod)
        {
            _custTableChangeProposal.CustPaymMode = this.PaymentMethod;
