    public void checkForUpdates()
    {
        if ((this.TimeMaterialBillable
           || this.FixedPriceBillable
           || this.InvestmentBillable
           || this.InternalCostBillable
           || this.TimeProjectsBillable
           || this.TimeMaterialEfficiency
           || this.FixedPriceEfficiency
           || this.InvestmentEfficiency
           || this.InternalCostEfficiency
           || this.TimeProjectsEfficiency)
            && !ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled())
        {
            this.createProjUtilTypes(ProjType::TimeMaterial,
                                    DMFEntityBase::string2Enum(this.TimeMaterialBillable, enumNum(ProjUtilOption)),
                                    DMFEntityBase::string2Enum(this.TimeMaterialEfficiency, enumNum(ProjUtilOption)));

            this.createProjUtilTypes(ProjType::FixedPrice,
                                    DMFEntityBase::string2Enum(this.FixedPriceBillable, enumNum(ProjUtilOption)),
                                    DMFEntityBase::string2Enum(this.FixedPriceEfficiency, enumNum(ProjUtilOption)));

            this.createProjUtilTypes(ProjType::Investment,
                                    DMFEntityBase::string2Enum(this.InvestmentBillable, enumNum(ProjUtilOption)),
                                    DMFEntityBase::string2Enum(this.InvestmentEfficiency, enumNum(ProjUtilOption)));

            this.createProjUtilTypes(ProjType::Internal,
                                   DMFEntityBase::string2Enum(this.InternalCostBillable, enumNum(ProjUtilOption)),
                                    DMFEntityBase::string2Enum(this.InternalCostEfficiency, enumNum(ProjUtilOption)));

            this.createProjUtilTypes(ProjType::Cost,
                                    DMFEntityBase::string2Enum(this.InternalCostBillable, enumNum(ProjUtilOption)),
                                    DMFEntityBase::string2Enum(this.InternalCostEfficiency, enumNum(ProjUtilOption)));

            this.createProjUtilTypes(ProjType::Time,
                                    DMFEntityBase::string2Enum(this.TimeProjectsBillable, enumNum(ProjUtilOption)),
                                    DMFEntityBase::string2Enum(this.TimeProjectsEfficiency, enumNum(ProjUtilOption)));
        }

        if (this.CapacityPlanningProduction || this.CapacityPlanningPlannedOrder)
        {
            this.createWrkCtrParameters(DMFEntityBase::string2Enum(this.CapacityPlanningProduction, enumNum(NoYes)),
                                        DMFEntityBase::string2Enum(this.CapacityPlanningPlannedOrder, enumNum(NoYes)));
        }

        if (this.IsHourSchedulingEnabled || this.DefaultWorkingCalendar)
        {
            this.createPSAParameters(DMFEntityBase::string2Enum(this.IsHourSchedulingEnabled, enumNum(NoYes)), this.DefaultWorkingCalendar);
        }
    }

    private void createProjUtilTypes(ProjType _projType, ProjUtilOption _billable, ProjUtilOption _efficiency)
    {
        ProjUtilTypes projUtilTypes;

        ttsbegin;

        select firstonly forUpdate projUtilTypes
            where projUtilTypes.ProjType == _projType;

        if (!projUtilTypes)
        {
            projUtilTypes.ProjType = _projType;
        }

        projUtilTypes.Billable      = _billable;
        projUtilTypes.Efficiency    = _efficiency;
        projUtilTypes.write();

        ttscommit;
    }

    public void createWrkCtrParameters(NoYes _production, NoYes _planningOrder)
    {
        WrkCtrParameters wrkCtrParameters;

        ttsbegin;

        select firstonly forupdate wrkCtrParameters;

        wrkCtrParameters.LimitCapProjectProd = _production;
        wrkCtrParameters.LimitCapProjectPO = _planningOrder;
        wrkCtrParameters.write();

        ttscommit;
    }

    public void createPSAParameters(NoYes _scheduling, str _calendar)
    {
        PSAParameters   psaParameters;

        ttsbegin;

        select firstonly forupdate psaParameters;

        psaParameters.EnableHourScheduling = _scheduling;
        psaParameters.SchedDefaultCalendar = _calendar;
        psaParameters.write();

        ttscommit;
    }

    public void postLoad()
    {
        // For exporting virtual fields
        [this.TimeMaterialBillable, this.TimeMaterialEfficiency] = this.getProjUtilTypes(ProjType::TimeMaterial);
        [this.FixedPriceBillable, this.FixedPriceEfficiency] = this.getProjUtilTypes(ProjType::FixedPrice);
        [this.InvestmentBillable, this.InvestmentEfficiency] = this.getProjUtilTypes(ProjType::Investment);
        [this.InternalCostBillable, this.InternalCostEfficiency] = this.getProjUtilTypes(ProjType::Cost);
        [this.TimeProjectsBillable, this.TimeProjectsEfficiency] = this.getProjUtilTypes(ProjType::Time);

        [this.CapacityPlanningProduction, this.CapacityPlanningPlannedOrder] = this.getWrkCtrParameters();

        [this.IsHourSchedulingEnabled, this.DefaultWorkingCalendar] = this.getPSAParameters();

        super();
    }

    private container getProjUtilTypes(ProjType _projType)
    {
        ProjUtilTypes   projUtilTypes;

        select firstonly Billable, Efficiency from projUtilTypes
            where projUtilTypes.ProjType == _projType;

        return [enum2Str(projUtilTypes.Billable), enum2Str(projUtilTypes.Efficiency)];
    }

    private container getWrkCtrParameters()
    {
        WrkCtrParameters   wrkCtrParameters;

        select firstonly wrkCtrParameters;

        return [enum2Str(wrkCtrParameters.LimitCapProjectProd), enum2Str(wrkCtrParameters.LimitCapProjectPO)];
    }

    private container getPSAParameters()
    {
        PSAParameters   psaParameters;

        select firstonly psaParameters;

        return [enum2Str(psaParameters.EnableHourScheduling), psaParameters.SchedDefaultCalendar];
    }

private void checkTrackingCommitmentsRelatedToBudgetReservation()
{
    if (this.GeneralBudgetReservation == NoYes::Yes
        && (this.PurchaseRequisition == NoYes::No
            || this.PurchaseOrder == NoYes::No 
            || this.VendorInvoice == NoYes::No))
    {
        throw error('@Budgeting_BudgetReservationPS:BudgetReservationRelatedTrackingCommitmentsError');
    }
}

    public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        boolean ret = true;

        if (BrazilParameters::isEnabled() && _dataSourceCtx.name() == dataEntityDataSourceStr(ProjectParameterEntity, ProjParameters))
        {
            ret = _dataSourceCtx.getBuffer().validateField(fieldnum(ProjParameters, ProjTransCFOPSameState_BR)) && ret;
            ret = _dataSourceCtx.getBuffer().validateField(fieldnum(ProjParameters, ProjTransCFOPOtherState_BR)) && ret;
            ret = _dataSourceCtx.getBuffer().validateField(fieldnum(ProjParameters, ProjTransCFOPOutsideCountry_BR)) && ret;

            if (!ret)
            {
                throw Exception::Error;
            }
        }        

        ret = super(_entityCtx, _dataSourceCtx);

        if (ret && _dataSourceCtx.name() == dataEntityDataSourceStr(ProjectParameterEntity, ProjParameters))
        {
            this.checkForUpdates();

            if (PublicSectorUtils::isBudgetReservationEnabled())
            {
                this.checkTrackingCommitmentsRelatedToBudgetReservation();
            }
        }

        return ret;
    }

