    public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        Common ret;

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, AccountingDistributionTemplate):
                ret = this.findAccountingDistributionTemplate();
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, DeliveryLogisticsPostalAddress):
                ret = TradeEntitySpecificDeliveryAddress::findLogisticsPostalAddressBaseEntityDataSource();
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, InvoiceLogisticsPostalAddress):
                ret = this.findInvoiceAddressForPurchaseOrderHeader();
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, PurchTable_W):
                ret = PurchTable_W::findByPurchTable(this.RecId, true);
                break;

            default:
                ret = super(_entityCtx, _dataSourceCtx);
        }

        return ret;
    }

    public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, PurchTable):
                PurchTable purchTable = _dataSourceCtx.getBuffer();

                if (this.isFieldSet(fieldNum(PurchPurchaseOrderHeaderEntity, AccountingDistributionTemplateName)))
                {
                    purchTable.AccountingDistributionTemplate = this.findAccountingDistributionTemplate().RecId;
                }

                if (this.scopeIdentifier())
                {
                    // Skip creation of markup when doing data migration
                    purchTable.SkipCreateMarkup = NoYes::Yes;
                }

                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, DeliveryLogisticsPostalAddress):
                if (_dataSourceCtx.isAnyEntityFieldSet(this))
                {
                    this.TradeEntitySpecificDeliveryAddress::mapLogisticsPostalAddressBaseEntityDataSourceToEntity(_dataSourceCtx);
                }

                break;
        }

        super(_entityCtx, _dataSourceCtx);

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, PurchTable):
                PurchTable purchTable = _dataSourceCtx.getBuffer();
                purchTable.fieldState(fieldnum(PurchTable, PurchStatus), FieldState::NotSet);
                break;
        }
    }

    public void mapDataSourceToEntity(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        if (_dataSourceCtx.name() == dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, PurchTable))
        {
            PurchTable purchTable = _dataSourceCtx.getBuffer();

            this.TradeEntitySpecificDeliveryAddress::mapSavedPostalAddressToEntity(
                _entityCtx,
                purchTable.DeliveryPostalAddress,
                dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, DeliveryLogisticsPostalAddress));

            if (this.DefaultLedgerDimension != purchTable.DefaultDimension)
            {
                DimensionSetEntity dimensionSet;

                select firstonly forupdate dimensionSet where
                    dimensionSet.RecId == purchTable.DefaultDimension;

                this.DefaultLedgerDimensionDisplayValue = dimensionSet.DisplayValue;

                DictDataEntity dictEntity = new DictDataEntity(tableNum(PurchPurchaseOrderHeaderEntity));
                int dataSourceNumber = dictEntity.fieldDatasource(fieldNum(PurchPurchaseOrderHeaderEntity, DefaultLedgerDimensionDisplayValue));

                this.fromViewDataSource(dataSourceNumber, dimensionSet);
            }
        }

        super(_entityCtx, _dataSourceCtx);
    }

    public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        super(_entityCtx, _dataSourceCtx);

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, PurchTable):
                // clear out 'isFieldSet' so the defaulting logic does not consider fields that were set by initValue as specified by the user
                _dataSourceCtx.setAllFieldStatesToNotSet();
                break;
        }
    }

    public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, PurchTable_W):
                // PurchTable_W was created during PurchTable.insert(), so need to resolve the conflict:
                _entityCtx.detectConflictForDatasource(_dataSourceCtx);
                break;
        }

        boolean ret = super(_entityCtx, _dataSourceCtx);

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, DeliveryLogisticsPostalAddress):
                this.TradeEntitySpecificDeliveryAddress::postInsertLogisticsPostalAddressBaseEntityDataSource(_dataSourceCtx);
                break;
        }

        return ret;
    }

    public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, PurchTable):
                if (this.CurrencyCode != this.orig().CurrencyCode)
                {
                    PurchTable purchTable = _dataSourceCtx.getBuffer();
                    purchTable.convertCurrencyCode(this.CurrencyCode, TradeCurencyConversion::TradeAgreements, purchTable.exchangeRateDateForCurrencyChange());
                }

                break;
        }

        return super(_entityCtx, _dataSourceCtx);
    }

    public boolean deleteEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderEntity, DeliveryLogisticsPostalAddress):
                _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                break;
        }

        return super(_entityCtx, _dataSourceCtx);
    }

    public void initValue()
    {
        if (!this.skipNumberSequenceCheck())
        {
            NumberSeqRecordFieldHandler::enableNumberSequenceControlForField(
                this, fieldNum(PurchPurchaseOrderHeaderEntity, PurchaseOrderNumber), PurchParameters::numRefPurchId());
        }

        super();
    }

    private LogisticsPostalAddress findInvoiceAddressForPurchaseOrderHeader()
    {
        LogisticsPostalAddress invoiceAddress;

        select firstonly invoiceAddress
            where invoiceAddress.CountryRegionId == this.InvoiceAddressCountryRegionId
                && invoiceAddress.Street == this.InvoiceAddressStreet
                && invoiceAddress.State == this.InvoiceAddressState
                && invoiceAddress.County == this.InvoiceAddressCounty
                && invoiceAddress.City == this.InvoiceAddressCity
                && invoiceAddress.ZipCode == this.InvoiceAddressZipCode;

        return invoiceAddress;
    }

private AccountingDistributionTemplate findAccountingDistributionTemplate()
{
    RecId legalEntityRecId = CompanyInfo::findDataArea(this.DataAreaId).RecId;
    return AccountingDistributionTemplate::findByNameAndLegalEntity(this.AccountingDistributionTemplateName, legalEntityRecId);
}

// This method implements the interface defined by <c>TradeEntitySpecificDeliveryAddress</c>
public RecId deliveryAddressPartyRecId()
{
    return CompanyInfo::current();
}

private static str isDeliveryAddressOrderSpecificComputedColumnDefinition()
{
    // The <c>IsDeliveryAddressOrderSpecific</c> field is only designed for import scenarios
    // so we will always return 'No' for export scnearios to save performance.
    return SysComputedColumn::returnLiteral(NoYes::No);
}

    public boolean validateWrite()
    {
        boolean ret;

        ret = super();

        if (this && !PurchTable::find(this.PurchaseOrderNumber).checkUpdate(true, true, true))
        {
            return false;
        }

        return ret;
    }
