    /// <summary>
    /// Updates the discount and totals for a given entity record.
    /// </summary>
    /// <param name = "_purchaseOrderHeaderEntityRecord">The purchase order header entity record.</param>
    /// <returns>The list of related entities that need to be resynced.</returns>
    /// <remarks>
    /// This method is aimed to be used by Dual Write to trigger price and discount calculation updates.
    /// See <c>RunDocumentAction</c> OData action on the <c>DualWriteProjectConfigurationEntity</c> entity.
    /// </remarks>
    public static List updatePrices(Common _purchaseOrderHeaderEntityRecord)
    {
        PurchPurchaseOrderHeaderV2Entity purchaseOrderHeaderEntityRecord = _purchaseOrderHeaderEntityRecord;
        List relatedRecords = new List(Types::Container);

        if (purchaseOrderHeaderEntityRecord != null)
        {
            purchaseOrderHeaderEntityRecord.calculatePrices();
            relatedRecords = purchaseOrderHeaderEntityRecord.getRecordsToSync();
        }

        return relatedRecords;
    }

    private void calculatePrices()
    {
        PurchTable purchTable = PurchTable::find(this.PurchaseOrderNumber);

        purchTable.updateFinalDisc();  /// Calculates the total discount for sales orders, purchase orders.
        purchTable.updateMultiLineDisc(); /// Updates the multiline discount for all lines within the given order.
    }

    private List getRecordsToSync()
    {
        List recordsToSync = new List(Types::Container);
        recordsToSync.addEnd([this.TableId, this.RecId]);

        PurchPurchaseOrderLineCDSEntity purchaseOrderLineEntityRecord;

        while select purchaseOrderLineEntityRecord
                where purchaseOrderLineEntityRecord.PurchaseOrderNumber == this.PurchaseOrderNumber
        {
            recordsToSync.addEnd([purchaseOrderLineEntityRecord.TableId, purchaseOrderLineEntityRecord.RecId]);
        }

        return recordsToSync;
    }

    public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        Common ret;

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, AccountingDistributionTemplate):
                ret = this.findAccountingDistributionTemplate();
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, DeliveryLogisticsPostalAddress):
                ret = TradeEntitySpecificDeliveryAddress::findLogisticsPostalAddressBaseEntityDataSource();
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, InvoiceLogisticsPostalAddress):
                ret = this.findInvoiceAddressForPurchaseOrderHeader();
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, PurchTable_W):
                ret = PurchTable_W::findByPurchTable(this.RecId, true);
                break;

            default:
                ret = super(_entityCtx, _dataSourceCtx);
        }

        return ret;
    }

    public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, PurchTable_BR):
                if (this.FiscalDocumentOperationTypeId)
                {
                    SalesPurchOperationType_BR salesPurchOperationType_BR;

                    select firstonly RecId from salesPurchOperationType_BR
                        where salesPurchOperationType_BR.OperationTypeID == this.FiscalDocumentOperationTypeId;

                    PurchTable_BR purchTable_BR = _dataSourceCtx.getBuffer();
                    purchTable_BR.SalesPurchOperationType_BR = salesPurchOperationType_BR.RecId;
                    _dataSourceCtx.setBuffer(purchTable_BR);
                }

                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, PurchTable):
                PurchTable purchTable = _dataSourceCtx.getBuffer();

                if (this.isFieldSet(fieldNum(PurchPurchaseOrderHeaderV2Entity, AccountingDistributionTemplateName)))
                {
                    purchTable.AccountingDistributionTemplate = this.findAccountingDistributionTemplate().RecId;
                }

                if (this.scopeIdentifier())
                {
                    // Skip creation of markup when doing data migration
                    purchTable.SkipCreateMarkup = NoYes::Yes;
                }

                this.setIntegration(purchTable);

                if (PurchParameters::find().IsSupplierShipmentDatesEnabled
                        && (    (this.isFieldSet(fieldNum(PurchPurchaseOrderHeaderV2Entity, RequestedShipDate))
                                 && this.isFieldSet(fieldNum(PurchPurchaseOrderHeaderV2Entity, RequestedDeliveryDate)))
                            ||  (this.isFieldSet(fieldNum(PurchPurchaseOrderHeaderV2Entity, ConfirmedShipDate))
                                 && this.isFieldSet(fieldNum(PurchPurchaseOrderHeaderV2Entity, ConfirmedDeliveryDate)))))
                {
                    purchTable.SkipShipReceiptDateCalculation = NoYes::Yes;
                }

                break;
            
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, DeliveryLogisticsPostalAddress):
                this.fieldState(fieldNum(PurchPurchaseOrderHeaderV2Entity, FormattedDeliveryAddress), FieldState::NotSet);
                
                if (_dataSourceCtx.isAnyEntityFieldSet(this))
                {
                    this.TradeEntitySpecificDeliveryAddress::mapLogisticsPostalAddressBaseEntityDataSourceToEntity(_dataSourceCtx);
                }

                break;
        }

        super(_entityCtx, _dataSourceCtx);

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, PurchTable):
                PurchTable purchTable = _dataSourceCtx.getBuffer();
                purchTable.fieldState(fieldnum(PurchTable, PurchStatus), FieldState::NotSet);
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, ReasonTableRef):
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, TransportationDocument):
                if (!_dataSourceCtx.isAnyFieldSetToNonDefaultValue(tableNum(PurchPurchaseOrderHeaderV2Entity)))
                {
                    _dataSourceCtx.setAllFieldStatesToNotSet();
                }
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, DeliveryLogisticsPostalAddress):
                // The Address field is generated to a specified format based on the component address parts. Updating it directly is not allowed.
                _dataSourceCtx.getBuffer().fieldState(fieldNum(LogisticsPostalAddressBaseEntity, Address), FieldState::NotSet);
                break;
        }
    }

private void setIntegration(PurchTable _purchTable)
{
    _purchTable.IsIntegration = TradeIntegrationType::DataEntity;
}

    public void mapDataSourceToEntity(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        if (_dataSourceCtx.name() == dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, PurchTable))
        {
            PurchTable purchTable = _dataSourceCtx.getBuffer();

            this.TradeEntitySpecificDeliveryAddress::mapSavedPostalAddressToEntity(
                _entityCtx,
                purchTable.DeliveryPostalAddress,
                dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, DeliveryLogisticsPostalAddress));

            if (this.DefaultLedgerDimension != purchTable.DefaultDimension)
            {
                DimensionSetEntity dimensionSet;

                select firstonly forupdate dimensionSet where
                    dimensionSet.RecId == purchTable.DefaultDimension;

                this.DefaultLedgerDimensionDisplayValue = dimensionSet.DisplayValue;

                DictDataEntity dictEntity = new DictDataEntity(tableNum(PurchPurchaseOrderHeaderV2Entity));
                int dataSourceNumber = dictEntity.fieldDatasource(fieldNum(PurchPurchaseOrderHeaderV2Entity, DefaultLedgerDimensionDisplayValue));

                this.fromViewDataSource(dataSourceNumber, dimensionSet);
            }
        }

        super(_entityCtx, _dataSourceCtx);
    }

    public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        super(_entityCtx, _dataSourceCtx);

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, PurchTable):
                // clear out 'isFieldSet' so the defaulting logic does not consider fields that were set by initValue as specified by the user
                _dataSourceCtx.setAllFieldStatesToNotSet();
                break;
        }
    }

    public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, PurchTable_W):
                // PurchTable_W was created during PurchTable.insert(), so need to resolve the conflict:
                _entityCtx.detectConflictForDatasource(_dataSourceCtx);
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, PurchTable_BR):
                // PurchTable_BR was created during PurchTable.insert(), so need to resolve the conflict:
                _entityCtx.detectConflictForDatasource(_dataSourceCtx);
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, TMSPurchTable):
                // TMSPurchTable might have been created during the insert or update of PurchTable
                _entityCtx.detectConflictForDatasource(_dataSourceCtx);
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, PurchTable):
                this.resolveFinTags(_entityCtx, _dataSourceCtx);
                break;
        }

        boolean ret = super(_entityCtx, _dataSourceCtx);

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, DeliveryLogisticsPostalAddress):
                this.TradeEntitySpecificDeliveryAddress::postInsertLogisticsPostalAddressBaseEntityDataSource(_dataSourceCtx);
                break;
        }

        return ret;
    }

    public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, PurchTable):
                if (this.CurrencyCode != this.orig().CurrencyCode)
                {
                    PurchTable purchTable = _dataSourceCtx.getBuffer();
                    purchTable.convertCurrencyCode(this.CurrencyCode, TradeCurencyConversion::TradeAgreements, purchTable.exchangeRateDateForCurrencyChange());
                }
                
                this.resolveFinTags(_entityCtx, _dataSourceCtx);
                break;

            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, TMSPurchTable):
                _entityCtx.detectConflictForDatasource(_dataSourceCtx);
                break;
        }

        return super(_entityCtx, _dataSourceCtx);
    }

    public boolean deleteEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(PurchPurchaseOrderHeaderV2Entity, DeliveryLogisticsPostalAddress):
                _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                break;
        }

        return super(_entityCtx, _dataSourceCtx);
    }

    public void initValue()
    {
        if (!this.skipNumberSequenceCheck())
        {
            NumberSeqRecordFieldHandler::enableNumberSequenceControlForField(
                this, fieldNum(PurchPurchaseOrderHeaderV2Entity, PurchaseOrderNumber), PurchParameters::numRefPurchId());
        }

        super();
    }

    private LogisticsPostalAddress findInvoiceAddressForPurchaseOrderHeader()
    {
        LogisticsPostalAddress invoiceAddress;

        select firstonly invoiceAddress
            where invoiceAddress.CountryRegionId == this.InvoiceAddressCountryRegionId
                && invoiceAddress.Street == this.InvoiceAddressStreet
                && invoiceAddress.State == this.InvoiceAddressState
                && invoiceAddress.County == this.InvoiceAddressCounty
                && invoiceAddress.City == this.InvoiceAddressCity
                && invoiceAddress.ZipCode == this.InvoiceAddressZipCode;

        return invoiceAddress;
    }

private AccountingDistributionTemplate findAccountingDistributionTemplate()
{
    RecId legalEntityRecId = CompanyInfo::findDataArea(this.DataAreaId).RecId;
    return AccountingDistributionTemplate::findByNameAndLegalEntity(this.AccountingDistributionTemplateName, legalEntityRecId);
}

