        return SysComputedColumn::if(SysComputedColumn::isNullExpression(SysComputedColumn::comparisonField(tableStr(SalesOrderHeaderEntity), identifierStr(SalesOrderTotals), fieldStr(SalesOrderTotals, RecId))),

            SysComputedColumn::returnLiteral(NoYes::No),
            SysComputedColumn::returnLiteral(NoYes::Yes));
    }

    public void initValue()
