        if (!this.skipNumberSequenceCheck())
        {
            NumberSeqRecordFieldHandler::enableNumberSequenceControlForField(
                this, fieldNum(SalesOrderHeaderEntity, SalesOrderNumber), SalesParameters::numRefSalesId());
