}

    /// <summary>
    /// Populates default values in a field.
    /// </summary>
    /// <param name = "_fieldId">The Id of the field that gets defaulted.</param>
    public void defaultField(FieldId _fieldId)
    {
        super (_fieldId);

        switch (_fieldId)
        {
            case fieldNum(SalesOrderHeaderEntity, DeliveryModeCode):
                this.defaultDlvMode();
                break;
            case fieldNum(SalesOrderHeaderEntity, DeliveryAddressDescription):
                if (!this.DeliveryAddressDescription && this.DeliveryAddressCountryRegionId)
                {
                    this.defaultDeliveryAddressDescription();
                }
                break;
        }
    }

private void defaultDeliveryAddressDescription()
{
    CustTable requestingCustomer = CustTable::find(this.OrderingCustomerAccountNumber);
    if (requestingCustomer.RecId)
    {
        this.DeliveryAddressDescription = requestingCustomer.name();
    }
}

    public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        super(_entityCtx, _dataSourceCtx);

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesOrderHeaderEntity, SalesTable):
                // clear out 'isFieldSet' so the defaulting logic does not consider fields that were set by initValue as specified by the user
                _dataSourceCtx.setAllFieldStatesToNotSet();
                break;
        }
    }

    public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        Common ret;

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesOrderHeaderEntity, LogisticsPostalAddressBaseEntity):
                ret = TradeEntitySpecificDeliveryAddress::findLogisticsPostalAddressBaseEntityDataSource();
                break;

            case dataEntityDataSourceStr(SalesOrderHeaderEntity, SalesTable_W):
                ret = SalesTable_W::findBySalesTable(this.RecId, true);
                break;

            case dataEntityDataSourceStr(SalesOrderHeaderEntity, TMSSalesTable):
                ret = TMSSalesTable::find(this.SalesOrderNumber, true);
                break;

            default:
                ret = super(_entityCtx, _dataSourceCtx);
        }

        return ret;
    }

    public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesOrderHeaderEntity, SalesTable):
                this.validateWriteSalesTableDataSource(_dataSourceCtx);
                break;

            case dataEntityDataSourceStr(SalesOrderHeaderEntity, TMSSalesTable):
                _entityCtx.detectConflictForDatasource(_dataSourceCtx);
                break;

            case dataEntityDataSourceStr(SalesOrderHeaderEntity, SalesTable_W):
                // SalesTable_W was created during SalesTable.insert(), so need to resolve the conflict:
                _entityCtx.detectConflictForDatasource(_dataSourceCtx);
                this.validateWriteDataSourceSalesTable_W(_dataSourceCtx);
                break;
        }

        boolean ret = super(_entityCtx, _dataSourceCtx);

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesOrderHeaderEntity, LogisticsPostalAddressBaseEntity):
                this.TradeEntitySpecificDeliveryAddress::postInsertLogisticsPostalAddressBaseEntityDataSource(_dataSourceCtx);
                break;
        }

        return ret;
    }

    public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesOrderHeaderEntity, SalesTable):
                if (this.CurrencyCode != this.orig().CurrencyCode)
                {
                    SalesTable salesTable = _dataSourceCtx.getBuffer();
                    salesTable.convertCurrencyCode(this.CurrencyCode, TradeCurencyConversion::TradeAgreements);
                }
                this.validateWriteSalesTableDataSource(_dataSourceCtx);
                break;

            case dataEntityDataSourceStr(SalesOrderHeaderEntity, SalesTable_W):
                this.validateWriteDataSourceSalesTable_W(_dataSourceCtx);
                break;

            case dataEntityDataSourceStr(SalesOrderHeaderEntity, TMSSalesTable):
                _entityCtx.detectConflictForDatasource(_dataSourceCtx);
                break;
        }

        return super(_entityCtx, _dataSourceCtx);
    }

    private void validateWriteSalesTableDataSource(DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        SalesTable salesTable = _dataSourceCtx.getBuffer() as SalesTable;
        SalesTableType salesTableType = salesTable.type();

        boolean isValid = true;

        if (this.isFieldModified(fieldNum(SalesOrderHeaderEntity, OrderingCustomerAccountNumber)) && !salesTableType.editCustAccount())
        {
            isValid = this.allowEditCheckFailed(fieldPName(SalesOrderHeaderEntity, OrderingCustomerAccountNumber));
        }

        if (this.isFieldModified(fieldNum(SalesOrderHeaderEntity, InvoiceCustomerAccountNumber)) && !salesTableType.editInvoiceAccount())
        {
            isValid = this.allowEditCheckFailed(fieldPName(SalesOrderHeaderEntity, InvoiceCustomerAccountNumber));
        }

        if (this.isFieldModified(fieldNum(SalesOrderHeaderEntity, TaxExemptNumber)) && !salesTableType.editVATNum())
        {
            isValid = this.allowEditCheckFailed(fieldPName(SalesOrderHeaderEntity, TaxExemptNumber));
        }

        if (this.isFieldModified(fieldNum(SalesOrderHeaderEntity, ArePricesIncludingSalesTax)) && !salesTableType.editInclTax())
        {
            isValid = this.allowEditCheckFailed(fieldPName(SalesOrderHeaderEntity, ArePricesIncludingSalesTax));
        }

        if (this.isFieldModified(fieldNum(SalesOrderHeaderEntity, CurrencyCode)) && !salesTableType.editCurrencyCode())
        {
            isValid = this.allowEditCheckFailed(fieldPName(SalesOrderHeaderEntity, CurrencyCode));
        }

        if (this.isFieldModified(fieldNum(SalesOrderHeaderEntity, DirectDebitMandateId)) && !CustPaymModeTable::find(salesTable.PaymMode).IsSEPA)
        {
            isValid = this.allowEditCheckFailed(fieldPName(SalesOrderHeaderEntity, DirectDebitMandateId));
        }

        if (salesTable.isMarkedForBankLC())
        {
            List fields = new List(Types::Integer);
            fields.addEnd(fieldNum(SalesOrderHeaderEntity, PaymentTermsName));
            fields.addEnd(fieldNum(SalesOrderHeaderEntity, FixedDueDate));
            fields.addEnd(fieldNum(SalesOrderHeaderEntity, CustomerPaymentMethodName));
            fields.addEnd(fieldNum(SalesOrderHeaderEntity, CustomerPaymentMethodSpecificationName));
            fields.addEnd(fieldNum(SalesOrderHeaderEntity, PaymentScheduleName));
            fields.addEnd(fieldNum(SalesOrderHeaderEntity, CashDiscountCode));
            fields.addEnd(fieldNum(SalesOrderHeaderEntity, CustomerTransactionSettlementType));
            isValid = isValid & this.checkFieldsAreNotModified(fields);
        }

        if (this.isFieldModified(fieldNum(SalesOrderHeaderEntity, WillAutomaticInventoryReservationConsiderBatchAttributes)) && !salesTable.canEditPdsBatchAttribAutoRes())
        {
            isValid = this.allowEditCheckFailed(fieldPName(SalesOrderHeaderEntity, WillAutomaticInventoryReservationConsiderBatchAttributes));
        }

        if (!isValid)
        {
            _dataSourceCtx.throwValidateWriteFailed();
        }
    }

    private void validateWriteDataSourceSalesTable_W(DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        CustParameters custParameters = CustParameters::find();
        SalesTable_W salesTable_w = _dataSourceCtx.getBuffer() as SalesTable_W;

        boolean isValid = true;

        if (this.isFieldModified(fieldNum(SalesOrderHeaderEntity, IsEntryCertificateRequired))
            && !custParameters.EntryCertificateManagementEnabled_W)
        {
            isValid = this.allowEditCheckFailed(fieldPName(SalesOrderHeaderEntity, IsEntryCertificateRequired));
        }

        if (this.isFieldModified(fieldNum(SalesOrderHeaderEntity, IsOwnEntryCertificateIssued))
            && !salesTable_w.EntryCertificateRequired_W
            && !custParameters.EntryCertificateIssuingEnabled_W)
        {
            isValid = this.allowEditCheckFailed(fieldPName(SalesOrderHeaderEntity, IsOwnEntryCertificateIssued));
        }

        if (!isValid)
        {
            _dataSourceCtx.throwValidateWriteFailed();
        }
    }

    public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesOrderHeaderEntity, LogisticsPostalAddressBaseEntity):
                if (_dataSourceCtx.isAnyEntityFieldSet(this))
                {
                    this.TradeEntitySpecificDeliveryAddress::mapLogisticsPostalAddressBaseEntityDataSourceToEntity(_dataSourceCtx);
                }

                break;
        }

        super(_entityCtx, _dataSourceCtx);

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesOrderHeaderEntity, SalesTable):

                SalesTable salesTable = _dataSourceCtx.getBuffer();

                // Setting the values here so the defaulting in the <c>initValue_Server</c> method in <c>SalesTable</c> is not used.
                // Table extension defaulting on SalesTable ensures that values set are not overridden if they are marked as set, so if the below 2 fields
                // are set on the entity (even if they have no value), then the corresponding SFKs on the sales table should also be marked as set, and the
                // entity framework is not doing this today (Dynamics: Bug 3667212).
                salesTable.fieldState(fieldNum(SalesTable, WorkerSalesTaker), this.fieldState(fieldNum(SalesOrderHeaderEntity, OrderTakerPersonnelNumber)));
                salesTable.fieldState(fieldNum(SalesTable, WorkerSalesResponsible), this.fieldState(fieldNum(SalesOrderHeaderEntity, OrderResponsiblePersonnelNumber)));

                if (this.isFieldSet(fieldNum(SalesOrderHeaderEntity, CreditNoteReasonCode)))
                {
                    DataEntityDataSourceRuntimeContext dataSourceCtx = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(SalesOrderHeaderEntity, CreditNoteReasonTableRef));

                    if (this.CreditNoteReasonCode)
                    {
                        salesTable.editReasonCode(true, this.CreditNoteReasonCode);

                        ReasonTableRef reasonTableRef = ReasonTableRef::find(salesTable.CreditNoteReasonCode);

                        if (!reasonTableRef.validateField(fieldNum(ReasonTableRef,Reason)))
                        {
                            throw error(strfmt("@ApplicationFoundation:DataEntitiesDataSourceValidateFieldFailed", fieldStr(SalesOrderHeaderEntity, CreditNoteReasonCode)));
                        }

                        if (reasonTableRef)
                        {
                            dataSourceCtx.setBuffer(reasonTableRef);
                        }
                    }

                    dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                }

                if (this.scopeIdentifier())
                {
                    // Skip creation of markup when doing data migration
                    salesTable.SkipCreateMarkup = NoYes::Yes;
                }
                break;
        }
    }

    public void mapDataSourceToEntity(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        super(_entityCtx, _dataSourceCtx);

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesOrderHeaderEntity, SalesTable):
                SalesTable salesTable = _dataSourceCtx.getBuffer();

                if (this.IsExportSale && !ExportSalesOrder::existSalesOrder(this.SalesOrderNumber))
                {
                    ExportSalesOrder::createFromSalesOrder(this.SalesOrderNumber);
                }
                if (!this.IsExportSale && ExportSalesOrder::existSalesOrder(this.SalesOrderNumber))
                {
                    ExportSalesOrder::DeleteBySalesOrder(this.SalesOrderNumber);
                }

                this.TradeEntitySpecificDeliveryAddress::mapSavedPostalAddressToEntity(_entityCtx,  salesTable.DeliveryPostalAddress);

                TMSSalesTable tmsSalesTable = TMSSalesTable::find(this.SalesOrderNumber);
                this.ShippingCarrierId = tmsSalesTable.CarrierCode;
                this.ShippingCarrierServiceId = tmsSalesTable.CarrierServiceCode;
                this.TransportationModeId = tmsSalesTable.ModeCode;
                break;
        }
    }

    public boolean validateWrite()
    {
        if (this.RecId)
        {
            if (ReverseChargeParameters_W::find().Enabled)
            {
                return checkFailed("@SCM:SalesOrderHeaderEntityDoesNotSupportUpdatesWhenReverseChargesAreEnabled");
            }

            if (RetailParameters::isRetailEnabledAndInUse()
                && RetailSalesTable::find(this.SalesOrderNumber).RetailChannel)
            {
                return checkFailed("@SCM:SalesOrderHeaderEntityDoesNotSupportUpdatesOfRetailOrders");
            }

            if (!SalesTable::find(this.SalesOrderNumber).checkUpdate(true, true, true))
            {
                return false;
            }
        }

        return super();
    }

    public boolean validateDelete()
    {
        if (!SalesTable::find(this.SalesOrderNumber).type().checkDelete(true, true))
        {
            return false;
        }

        return super();
    }

