Method.Source:
    public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        Common ret;

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesOrderLineV2Entity, EcoResCategory):
                ret = this.resolveProductCategory();
                break;
            case dataEntityDataSourceStr(SalesOrderLineV2Entity, InventDim):
                DataEntityDataSourceRuntimeContext lineCtx = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine));
                SalesLine line = lineCtx.getBuffer();

                ret = InventDim::find(line.InventDimId);
                break;

            case dataEntityDataSourceStr(SalesOrderLineV2Entity, EcoResCategory_IntrastatCommodity):
                if (this.IntrastatCommodityCode)
                {
                    ret = EcoResCategory::findByCode(this.IntrastatCommodityCode, IntrastatParameters::find().CommodityHierarchy);
                }
                break;

            case dataEntityDataSourceStr(SalesOrderLineV2Entity, LogisticsPostalAddressBaseEntity):
                ret = TradeEntitySpecificDeliveryAddress::findLogisticsPostalAddressBaseEntityDataSource();
                break;

            case dataEntityDataSourceStr(SalesOrderLineV2Entity, FiscalDocumentType_BR):
                FiscalEstablishmentId_BR fiscalEstablishmentId_BR = InventSite::find(this.ShippingSiteId).fiscalEstablishment().FiscalEstablishmentId;
                ret = FiscalDocumentType_BR::find(this.FiscalDocumentTypeId , fiscalEstablishmentId_BR, true);
                break;

            default:
                ret = super(_entityCtx, _dataSourceCtx);
        }

        return ret;
    }

    public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        boolean recalcShippingDateRequested = false;
        boolean recalcReceiptDateRequested = false;

        using (var activityContext = SalesInstrumentationLogger::createLogger(tableStr(SalesOrderLineV2Entity)).salesOrderActivities().insertEntityDataSource(_dataSourceCtx))
        {
            switch (_dataSourceCtx.name())
            {
                case dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine):
                    SalesLine salesLine = _dataSourceCtx.getBuffer();
                    salesLine = this.setProjectFields(salesLine);

                    if (this.DefaultLineNumberFromLineCreationSequenceNumber
                        && SalesDefaultLineNumberFromLineSequenceNumberFlight::instance().isEnabled())
                    {
                        salesLine.DefaultLineNumberFromLineCreationSequenceNumber = this.DefaultLineNumberFromLineCreationSequenceNumber;

                        salesLine.LineNum = this.LineCreationSequenceNumber;
                    }
                    else
                    {
                        salesLine.LineNum = salesLine.LineNum ? salesLine.LineNum : SalesLine::lastLineNum(this.SalesOrderNumber) + 1;
                    }

                    this.initializeDefaultInventDimForDirectDelivery(salesLine);

                    if (SalesOrderLineEntityUpdateShippingDateReceiptDateRecalculatedFlight::instance().isEnabled())
                    {
                        if (this.isFieldSet(fieldNum(SalesOrderLineV2Entity, RequestedShippingDate)) &&
                           !this.isFieldSet(fieldNum(SalesOrderLineV2Entity, RequestedReceiptDate)))
                        {
                            recalcReceiptDateRequested = true;
                        }

                        if (this.isFieldSet(fieldNum(SalesOrderLineV2Entity, RequestedReceiptDate)) &&
                           !this.isFieldSet(fieldNum(SalesOrderLineV2Entity, RequestedShippingDate)))
                        {
                            recalcShippingDateRequested = true;
                        }
                    }

                    salesLine.defaultField(fieldNum(SalesLine, DeliveryType));
                    salesLine.defaultField(fieldNum(SalesLine, SourcingVendAccount));
                    salesLine.defaultField(fieldNum(SalesLine, SourcingOrigin));
                    salesLine.defaultField(fieldNum(SalesLine, SourcingCompanyId));
                    salesLine.defaultField(fieldNum(SalesLine, SourcingInventSiteId));
                    salesLine.defaultField(fieldNum(SalesLine, SourcingInventLocationId));
                    
                    if (salesLine.ItemId && SalesParameters::find().DeferDirectDeliveryCreation == true)
                    {
                        salesLine.initDropShipAndInstallmentEligible(salesLine.inventTable());
                    }
                    this.validateWriteSalesLineDataSource(_dataSourceCtx);

                    break;

                case dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine_BR):
                    // SalesLine_BR was created during SalesLine.insert(), so need to resolve the conflict:
                    _entityCtx.detectConflictForDatasource(_dataSourceCtx);
                    break;

                case dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLineForeignTradeCategory):
                    _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                    break;
            }

            if (_dataSourceCtx.name() == dataEntityDataSourceStr(SalesOrderLineV2Entity, RetailSalesLine))
            {
                RetailSalesLine retailSalesLine = _dataSourceCtx.getBuffer();
                RetailSalesLine retailSalesLineExt = RetailSalesLine::findBySalesLine(retailSalesLine.SalesLine, true);
                delete_from retailSalesLineExt where retailSalesLineExt.SalesLine == retailSalesLine.SalesLine;

                SalesTable salesOrder = SalesTable::find(SalesLine::findRecId(retailSalesLine.SalesLine).SalesId);
                if(!salesOrder.RetailChannelTable)
                {
                    _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                }

                if (RetailSalesLineEntityRecalcLineManualDiscountPercentageFlight::instance().isEnabled()
                    && (this.isFieldModified(fieldNum(SalesOrderLineV2Entity, RetailCalculatedManualLineDiscountPercentage))
                    || this.isFieldModified(fieldNum(SalesOrderLineV2Entity, RetailCalculatedManualLineDiscountAmount))))
                {
                    DataEntityDataSourceRuntimeContext lineCtx = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine));
                    SalesLine salesLine = lineCtx.getBuffer();
                    SalesTable salesTable = SalesTable::find(salesLine.salesId);
                    if (this.shouldRecalculateWhenUpdateManualLineDiscountPercentage(salesTable))
                    {
                        retailSalesLine = _dataSourceCtx.getBuffer();
                        salesLine.SkipPriceDiscCalc = NoYes::No;
                        SalesLine.retailPackRetailSalesLine(retailSalesLine);
                        salesLine.setPriceDisc(salesLine.inventDim(), true);
                        salesLine.doUpdate();
                        if (!this.isEntityInDataManagementContext() && RetailPricingCustomizableFeatureToggle::isUpdateDiscountInPriceDetailsForOdata())
                        {
                            salesLine.updateRetailDiscounts();
                            _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                        }
                    }
                }             
            }

            boolean ret;
            using (RetailSalesOrderCreationContext context = RetailSalesOrderCreationContext::construct())
            {
                context.parmDataImporting(true);
                context.parmIsInDataManagementContext(this.isEntityInDataManagementContext());
                ret = super(_entityCtx, _dataSourceCtx);
            }

            switch (_dataSourceCtx.name())
            {
                case dataEntityDataSourceStr(SalesOrderLineV2Entity, LogisticsPostalAddressBaseEntity):
                    this.TradeEntitySpecificDeliveryAddress::postInsertLogisticsPostalAddressBaseEntityDataSource(_dataSourceCtx);
                    break;

                case dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine):
                    boolean isSalesLineForUpdate = SalesOrderLineEntityUpdateShippingDateReceiptDateRecalculatedFlight::instance().isEnabled()
                        && (recalcReceiptDateRequested || recalcShippingDateRequested);

                    SalesLine salesLine = SalesLine::findInventTransId(this.InventoryLotId, isSalesLineForUpdate);

                    if (salesLine.isCategoryBased())
                    {
                        SalesTableSalesLineDataSourceWriting salesLineWriting = SalesTableSalesLineDataSourceWriting::newFromSalesLine(salesLine);
                        SalesLineForeignTradeCategory salesLineForeignTradeCategory;
                        salesLineForeignTradeCategory.NGPCodesTable_FR = this.NGPCode;
                        salesLineWriting.updateForeignTradeCategory(salesLineForeignTradeCategory);
                    }

                    if (isSalesLineForUpdate)
                    {
                        if (recalcReceiptDateRequested)
                        {
                            SalesShippingDateRequested shippingDateRequested = salesLine.ShippingDateRequested;
                            salesLine.modifiedFieldDDC(fieldnum(SalesLine, ShippingDateRequested));
                            salesLine.ShippingDateRequested = shippingDateRequested;
                            salesLine.doUpdate();
                        }

                        if (recalcShippingDateRequested)
                        {
                            SalesReceiptDateRequested receiptDateRequested = salesLine.ReceiptDateRequested;
                            salesLine.modifiedFieldDDC(fieldnum(SalesLine, ReceiptDateRequested));
                            salesLine.ReceiptDateRequested = receiptDateRequested;
                            salesLine.doUpdate();
                        }
                    }
                
                    if (CustParameters::find().IsTotalsCalculationAutomatic)
                    {
                        SalesTable salesTable = salesLine.salesTable(true);
                    
                        if (salesTable.RecId && salesTable.setEstimate())
                            salesTable.doUpdate();
                    }

                    this.updateAppliedDiscountDetails(salesLine);

                    break;
            }

            return ret;
        }
    }

    public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        using (var activityContext = SalesInstrumentationLogger::createLogger(tableStr(SalesOrderLineV2Entity)).salesOrderActivities().updateEntityDataSource(_dataSourceCtx))
        {
            switch (_dataSourceCtx.name())
            {
                case dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine):
                    SalesLine salesLine = _dataSourceCtx.getBuffer();
                    if (salesLine.LineDeliveryType == LineDeliveryType::OrderLineWithMultipleDeliveries)
                    {
                        SalesTableForm_DeliverySchedule salesTableForm = new SalesTableForm_DeliverySchedule(salesLine);
                        salesTableForm.deliveryLinesSync(salesLine, false);
                    }
                    this.validateWriteSalesLineDataSource(_dataSourceCtx);
                    this.defaultLineDimension(salesLine);
                
                    // SalesLine.CustomerRef was updated during salesTable.initializeSalesLineOnUpdateSalesTable(), so need to resolve the conflict.
                    if (this.RecVersion != salesLine.RecVersion)
                    {
                        this.fromViewDataSource(_dataSourceCtx.id(), salesLine);
                    }

                    this.updateAppliedDiscountDetails(salesLine);                  

                    break;
            }

            if (RetailSalesLineEntityRecalcLineManualDiscountPercentageFlight::instance().isEnabled()
                && _dataSourceCtx.name() == dataEntityDataSourceStr(SalesOrderLineV2Entity, RetailSalesLine)
                && (this.isFieldModified(fieldNum(SalesOrderLineV2Entity, RetailCalculatedManualLineDiscountPercentage))
                || this.isFieldModified(fieldNum(SalesOrderLineV2Entity, RetailCalculatedManualLineDiscountAmount))))
            {
                DataEntityDataSourceRuntimeContext lineCtx = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine));
                SalesLine salesLine = lineCtx.getBuffer();
                SalesTable salesTable = SalesTable::find(salesLine.salesId);
                if (this.shouldRecalculateWhenUpdateManualLineDiscountPercentage(salesTable))
                {
                    RetailSalesLine retailSalesLine = _dataSourceCtx.getBuffer();
                    SalesLine.retailPackRetailSalesLine(retailSalesLine);
                    salesLine.setPriceDisc(salesLine.inventDim(), true);
                    salesLine.doUpdate();
                    if (!this.isEntityInDataManagementContext() && RetailPricingCustomizableFeatureToggle::isUpdateDiscountInPriceDetailsForOdata())
                    {
                        salesLine.updateRetailDiscounts();
                        _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                    }
                }
            }

            boolean ret;
            using (RetailSalesOrderCreationContext context = RetailSalesOrderCreationContext::construct())
            {
                context.parmDataImporting(true);
                context.parmIsInDataManagementContext(this.isEntityInDataManagementContext());

                ret = super(_entityCtx, _dataSourceCtx);

                switch (_dataSourceCtx.name())
                {
                    case dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine):
                        if (CustParameters::find().IsTotalsCalculationAutomatic)
                        {
                            SalesLine  salesLine = _dataSourceCtx.getBuffer();
                            SalesTable salesTable = salesLine.salesTable(true);

                            if (salesTable.RecId && salesTable.setEstimate())
                                salesTable.doUpdate();
                        }
                
                        break;
                }
            }

            return ret;
        }
    }

    private void defaultLineDimension(SalesLine _salesLine)
    {
        if (!_salesLine.DefaultDimension && !_salesLine.isFieldSet(fieldNum(SalesLine, DefaultDimension)))
        {
            _salesLine.DefaultDimension = _salesLine.copyDimension(SalesTable::find(_salesLine.SalesId).DefaultDimension);
		}
    }

    public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesOrderLineV2Entity, LogisticsPostalAddressBaseEntity):
				this.fieldState(fieldNum(SalesOrderLineV2Entity, FormattedDelveryAddress), FieldState::NotSet);

                if (_dataSourceCtx.isAnyEntityFieldSet(this))
                {
                    this.TradeEntitySpecificDeliveryAddress::mapLogisticsPostalAddressBaseEntityDataSourceToEntity(_dataSourceCtx);
                }
                break;
            case dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine):
				SalesLine line = _dataSourceCtx.getBuffer() as SalesLine;

                this.setIntegration(line);

                if (_entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert
                    || _entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Update)
                {
                    if (SalesParameters::find().SalesEntityCalculateLineAmount
                        || !this.isFieldSet(fieldNum(SalesOrderLineV2Entity, LineAmount)))
                    {
                        // Will clear the change policy without a dialog.
                        if (line.Type().canLineAmountBeUpdatedWithNonInteractiveSession())
                        {
                            this.fieldState(fieldNum(SalesOrderLineV2Entity, LineAmount), FieldState::NotSet);
                        }
                    }
                    else
                    {
                        this.resetLineAmountDependentFieldStages();
                    }
                }
                break;
            case dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine_Intrastat):
                if (_entityCtx.getDatabaseOperation() != DataEntityDatabaseOperation::None
                    && SalesOrderLineEntitySkipIntrastatFlight::instance().isEnabled())
                {
                    this.fieldState(fieldNum(SalesOrderLineV2Entity, IntrastatSpecialMovementCode), FieldState::NotSet);
                    _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                }
                break;
        }

        super(_entityCtx, _dataSourceCtx);

        guid blankGuid; // the default value is used as a check below:

        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine):
                SalesLine line = _dataSourceCtx.getBuffer();
                line = this.setProjectFields(line);
                switch (_entityCtx.getDatabaseOperation())
                {
                    case DataEntityDatabaseOperation::Insert:
                        if (this.InventoryLotId && this.isFieldSet(fieldNum(SalesOrderLineV2Entity, InventoryLotId)))
                        {
                            // Without skipping this validation, validateField will fail on the mapped field.
                            this.skipDataSourceValidateField(fieldNum(SalesOrderLineV2Entity, InventoryLotId), true);
                        }
                        line.SkipAssignInventTransId = NoYes::Yes;
                        SalesTable salesTable = SalesTable::find(this.SalesOrderNumber);
                        line.CustAccount = salesTable.CustAccount;
                        line.CustGroup = salesTable.CustGroup;

                        if (this.LineNumber && this.isFieldSet(fieldNum(SalesOrderLineV2Entity, LineNumber)))
                        {
                            this.skipDataSourceValidateField(fieldNum(SalesOrderLineV2Entity, LineNumber), true);
                        }  
                      
                        if (!this.isFieldSet(fieldNum(SalesOrderLineV2Entity, ExcludeFromMasterPlanning))
                            && salesTable.MpsExcludeSalesOrder)
                        {
                            line.MpsExcludeSalesLine = salesTable.MpsExcludeSalesOrder;
                        }

                    // Continue to fall through to next case:
                    case DataEntityDatabaseOperation::Update:
                        // Set the correct inventDimId on the table buffer.
                        line.parmQuantityErrorCorrectionMethods(InventMovementQuantityErrorCorrectionMethods::acceptUserInputWithWarning());

                        line.setStockedProduct();
                        line.InventDimId = this.InventInventoryDimensionEntityFieldsMapping::resolveInventDimId();
                        line.AutoCreateInterCompanyOrders = NoYes::Yes;

                        if (this.isFieldSet(fieldNum(SalesOrderLineV2Entity, CreditNoteReasonCode)))
                        {
                            line.editReasonCode(true, this.CreditNoteReasonCode);

                            ReasonTableRef reasonTableRef = ReasonTableRef::find(line.CreditNoteReasonCode);
                            if (!reasonTableRef.validateField(fieldNum(ReasonTableRef,Reason)))
                            {
                                throw error(strfmt("@ApplicationFoundation:DataEntitiesDataSourceValidateFieldFailed", fieldStr(SalesOrderLineV2Entity, CreditNoteReasonCode)));
                            }

                            DataEntityDataSourceRuntimeContext dataSourceCtx = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(SalesOrderLineV2Entity, CreditNoteReasonTableRef));

                            if (reasonTableRef)
                            {
                                dataSourceCtx.setBuffer(reasonTableRef);
                                dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                            }
                        }

                        // When SkipCreateAutoCharges is specified, the SkipCreateMarkup field on salesline should be set to the value of the SkipCreateAutoCharges field on the entity
                        if (this.isFieldSet(fieldNum(SalesOrderLineV2Entity, SkipCreateAutoCharges)))
                        {
                            line.SkipCreateMarkup = this.SkipCreateAutoCharges;
                        }
                        else
                        {
                            // When SkipCreateAutoCharges is not specified,keep the original rule
                            if (this.scopeIdentifier())
                            {
                                // Skip creation of markup when doing data migration
                                line.SkipCreateMarkup = NoYes::Yes;
                            }
                        }
      
                        if (this.scopeIdentifier() || SalesOrderLineEntitySkipCalculationFlight::instance().isEnabled())
                        {
                            line.SkipPriceDiscCalcOnImport = !SalesParameters::find().ApplyPriceDiscOnSalesLineEntity;
                        }

                        if (this.scopeIdentifier() != blankGuid)
                        {
                            // And propagate the scopeIdentifier to the datasource:
                            line.scopeIdentifier(this.scopeIdentifier());
                        }
                       
                        if (this.isFieldSet(fieldNum(SalesOrderLineV2Entity, ExcludeFromMasterPlanning)))
                        {
                            line.MpsUpdateExcludeSalesLine = NoYes::Yes;
                        }

                        break;
                }
                break;
            case dataEntityDataSourceStr(SalesOrderLineV2Entity, MCRSalesLine):
                this.skipDataSourceValidateField(fieldNum(SalesOrderLineV2Entity, GiftCardNumber), true);
                break;
            case dataEntityDataSourceStr(SalesOrderLineV2Entity, CreditNoteReasonTableRef):
                ReasonTableRef reasonTableRef = _dataSourceCtx.getBuffer();
                if (reasonTableRef.Reason == '' && reasonTableRef.ReasonComment == '')
                {
                    _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                }
                break;
            case dataEntityDataSourceStr(SalesOrderLineV2Entity, LogisticsPostalAddressBaseEntity):
                // The Address field is generated to a specified format based on the component address parts. Updating it directly is not allowed.
				_dataSourceCtx.getBuffer().fieldState(fieldNum(LogisticsPostalAddressBaseEntity, Address), FieldState::NotSet);
                break;
        }
    }

private void setIntegration(SalesLine _salesLine)
{
    if (this.IsCDSIntegration)
    {
        _salesLine.IsIntegration = TradeIntegrationType::CDS;
    }
    else
    {
        _salesLine.IsIntegration = TradeIntegrationType::DataEntity;
    }
}

    public void mapDataSourceToEntity(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        if (_dataSourceCtx.name() == dataEntityDataSourceStr(SalesOrderLineV2Entity, SalesLine))
        {
            SalesLine salesLine = _dataSourceCtx.getBuffer();
            this.TradeEntitySpecificDeliveryAddress::mapSavedPostalAddressToEntity(_entityCtx,  salesLine.DeliveryPostalAddress);
            this.InventInventoryDimensionEntityFieldsMapping::initFromInventDim(salesLine.inventDim());
            this.mapSalesCategoryFromSalesLineToEntity(_entityCtx, salesLine);
            this.mapDefaultLedgerDimensionFromSalesLineToEntity(_entityCtx, salesLine);

            if (this.IsCDSIntegration)            
            {
                if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled())
                {
                    salesLine.fieldState(fieldNum(SalesLine, SalesPrice), FieldState::Set);
                    salesLine.fieldState(fieldNum(SalesLine, LineAmount), FieldState::Set);
                }

                if (DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled())
                {
                    salesLine.fieldState(fieldNum(SalesLine, LineDisc), FieldState::Set);
                    salesLine.fieldState(fieldNum(SalesLine, LinePercent), FieldState::Set);
                }
            }           
        }

        super(_entityCtx, _dataSourceCtx);
    }

    public boolean validateWrite()
    {
        SalesTable salesTable = SalesTable::find(this.SalesOrderNumber);

        if (SalesOrderLineV2EntityValidateWriteFlight::instance().isEnabled() && !salesTable.RecId)
        {
            return checkFailed(strFmt("@SCM:SpecifiedSalesOrderCouldNotBeFoundOperationAbortedError", this.SalesOrderNumber));
        }

        if (this.RecId)
        {
            if (RetailParameters::isRetailEnabledAndInUse()
                && RetailSalesTable::find(this.SalesOrderNumber).RetailChannel)
            {
                return checkFailed("@SCM:SalesOrderLineEntityDoesNotSupportUpdatesOfRetailOrders");
            }
        }

        if (!salesTable.checkUpdate(true, true, true))
        {
            return false;
        }

        boolean ret;

        ret = super();

        ret = ret && InventTable::checkExist(this.ItemNumber);

        if (ret && this.mustValidateSalesProductCategory())
        {
            EcoResProductCategory productCategory = EcoResProductCategory::findByItemIdCategoryHierarchyRole(this.ItemNumber, EcoResCategoryNamedHierarchyRole::Sales);
            EcoResCategory selectedCategory = this.resolveProductCategory();

            if (selectedCategory.RecId != productCategory.Category)
            {
                ret = checkFailed(strFmt("@SCM:SalesLineItemAndCategorySpecifiedError", this.ItemNumber, this.SalesProductCategoryName));
            }
        }

        if (ret)
        {
            InventDim userSpecifiedDimensions = InventDim::find(this.InventInventoryDimensionEntityFieldsMapping::resolveInventDimId());
            Set allowEmptyFiels = new Set(Types::Integer);
            allowEmptyFiels.add(-1); // checkDimFieldsValid should allow empty values for all fields since we haven't done defaulting yet.
			ret = userSpecifiedDimensions.checkDimFieldsValid(this.ItemNumber, userSpecifiedDimensions, true, allowEmptyFiels);
        }

        return ret;
    }

private boolean mustValidateSalesProductCategory()
{
    return this.ItemNumber && this.SalesProductCategoryName
        && (this.IsCDSIntegration == NoYes::No || !SalesOrderQuoteLineCDSCheckProductCategoryOnlyIfModifiedFlight::instance().isEnabled() || this.isFieldModified(fieldNum(SalesOrderLineV2Entity, SalesProductCategoryName)));
}

    public boolean validateDelete()
    {
        if (!SalesLine::findInventTransId(this.InventoryLotId).checkDelete(true, true))
        {
            return false;
        }

        return super();
    }

    private void validateWriteSalesLineDataSource(DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        SalesLine salesLine = _dataSourceCtx.getBuffer() as SalesLine;

        boolean isValid = true;

        if (this.isFieldModified(fieldNum(SalesOrderLineV2Entity, WillRebateCalculationExcludeLine))
            && !salesLine.canEditPdsRebate())
        {
            isValid = this.allowEditCheckFailed(fieldPName(SalesOrderLineV2Entity, WillRebateCalculationExcludeLine));
        }

        if (this.isFieldModified(fieldNum(SalesOrderLineV2Entity, WillAutomaticInventoryReservationConsiderBatchAttributes))
            && !salesLine.canEditPdsBatchAttribAutoRes())
        {
            isValid = this.allowEditCheckFailed(fieldPName(SalesOrderLineV2Entity, WillAutomaticInventoryReservationConsiderBatchAttributes));
        }

        if (this.isFieldModified(fieldNum(SalesOrderLineV2Entity, OrderedCatchWeightQuantity))
            && !InventTable::find(salesLine.ItemId).pdsCWItem())
        {
            isValid = salesLine.PdsCWQty == 0;
        }

        if (!isValid)
        {
            _dataSourceCtx.throwValidateWriteFailed();
        }
    }

    private EcoResCategory resolveProductCategory()
    {
        EcoResCategory category;
        EcoResCategoryHierarchyRole hierarchyRole;

        select firstonly hierarchyRole where hierarchyRole.NamedCategoryHierarchyRole == EcoResCategoryNamedHierarchyRole::Sales;
        if (hierarchyRole)
        {
            category = EcoResCategory::findByName(this.SalesProductCategoryName, hierarchyRole.CategoryHierarchy);
        }

        return category;
    }

