        super();
    }
}

    /// <summary>
    /// initialize default inventDim for direct delivery invent from sales header or default warehouse in 
    /// item is marked for direct delivery when inventDim not setting up.
    /// </summary>
    /// <param name = "_salesLine">Sales line record.</param>
    protected void initializeDefaultInventDimForDirectDelivery(SalesLine _salesLine)
    {
        MCRInventTable mcrInventTable;

        select firstonly DropShipment from mcrInventTable
            where mcrInventTable.InventTable == _salesLine.inventTable().RecId;
        
        InventDim inventDim = _salesLine.inventDim();

        if (mcrInventTable.DropShipment && (!inventDim.InventSiteId || !inventDim.InventLocationId))
        {
                if (SalesOrderLineV2EntityDefaultDimensionsV2Flight::instance().isEnabled())
                {
                    this.defaultLineDimension(_salesLine);
                    _salesLine.type().initReleasedProductSpecificDefaulting(_salesLine.inventTable());
                }

            _salesLine.type().initStorageDimensionsFromHeader();

            if (SalesOrderLineV2EntityDefaultDimensionsV2Flight::instance().isEnabled())
            {
                if (mcrInventTable.DefaultDropShipmentWarehouse)
                {
                    InventDim inventDimLocal = _salesLine.inventDim();
                    inventDimLocal.InventLocationId = mcrInventTable.DefaultDropShipmentWarehouse;
                    inventDimLocal.InventSiteId     = InventLocation::find(inventDimLocal.InventLocationId).InventSiteId;
                    inventDimLocal = InventDim::findOrCreate(inventDimLocal);
                    _salesLine.setInventDimId(inventDimLocal.inventDimId, inventDimLocal);              
                }
            }
        }
    }

/// <summary>
/// Updates details of applied discounts which show in line price details.
/// </summary>
/// <param name = "_salesLine">Sales line record.</param>
private void updateAppliedDiscountDetails(SalesLine _salesLine)
{
    // Don't do this during data management which will degrade performance.
    if (!this.isEntityInDataManagementContext() &&
        RetailPricingCustomizableFeatureToggle::isUpdateDiscountInPriceDetailsForOdata())
    {
        if (RetailSalesLine::findBySalesLine(_salesLine.RecId))
        {
            _salesLine.updateRetailDiscounts();
        }
    }
}

private static str isCalculateLineAmountForced()
{
    // The <c>CalculateLineAmount</c> field is designed to be enabled
    // so we will always return 'Yes' for user to change otherwise
    return SysComputedColumn::returnLiteral(NoYes::Yes);
}

private void resetLineAmountDependentFieldStages()
{
    this.fieldState(fieldNum(SalesOrderLineV2Entity, SalesPrice), FieldState::NotSet);
    this.fieldState(fieldNum(SalesOrderLineV2Entity, LineDiscountAmount), FieldState::NotSet);
    this.fieldState(fieldNum(SalesOrderLineV2Entity, LineDiscountPercentage), FieldState::NotSet);
    this.fieldState(fieldNum(SalesOrderLineV2Entity, MultiLineDiscountAmount), FieldState::NotSet);
    this.fieldState(fieldNum(SalesOrderLineV2Entity, MultiLineDiscountPercentage), FieldState::NotSet);
    this.fieldState(fieldNum(SalesOrderLineV2Entity, FixedPriceCharges), FieldState::NotSet);
}

    /// <summary>
    /// Executes the logic once after processing the target data.
    /// </summary>
    /// <param name="_dmfDefinitionGroupExecution">
    /// A table buffer.
    /// </param>
    public static void postTargetProcess(DMFDefinitionGroupExecution _dmfDefinitionGroupExecution)
    {
        if (RetailPricingKillSwitch::calculateSalesLineWhenImport())
        {
            RetailParameters retailParameters = RetailParameters::find();
            // When full pricing calculation is delayed during line by line import, recalculate all related sales orders at the end of import job.
            if (retailParameters.EnableDelayedPriceAndDiscountCalculation)
            {
                DMFDefinitionGroupName definitionGroup =  _dmfDefinitionGroupExecution.DefinitionGroup;
                DMFExecutionId executionId = _dmfDefinitionGroupExecution.ExecutionId;
                DMFEntityName entityName = _dmfDefinitionGroupExecution.Entity;
                DMFDefinitionGroupExecution currentExecution;

                ttsbegin;

                // Add a lock here so the multiple threads will run in sequence.
                select pessimisticlock currentExecution
                where currentExecution.DefinitionGroup == definitionGroup
                    && currentExecution.ExecutionId == executionId
                    && currentExecution.Entity == entityName;

                // Check if import job is finished.
                if (currentExecution.StagingStatus == DMFBatchJobStatus::Finished)
                {
                    SalesOrderLineV2Staging salesOrderLineV2Staging;
                    while select SalesOrderNumber from salesOrderLineV2Staging
                        group by SalesOrderNumber
                        where salesOrderLineV2Staging.DefinitionGroup == definitionGroup
                            && salesOrderLineV2Staging.ExecutionId == executionId
                    {
                        SalesTable salesTable = SalesTable::find(salesOrderLineV2Staging.SalesOrderNumber);
                        RetailSalesTable retailSales = salesTable.retailSalesTable();

                        boolean isRetailOrder = RetailPricingEngine::useRetailPricingEngineForSalesOrder(salesTable.SalesId);
                        if (isRetailOrder)
                        {
                            RetailSalesOrderCalculator::setPricesDiscountsOnOrderOnServer(salesTable.SalesId, retailSales.TotalManualDiscountPercentage);
                        }
                    }
                }

                ttscommit;
            }
        }
    }

    public static void postGetStagingData(DMFDefinitionGroupExecution _dmfDefinitionGroupExecution)
    {
        SalesOrderLineV2Staging salesOrderLineV2Staging;

        select firstonly count(RecId) from salesOrderLineV2Staging
            where salesOrderLineV2Staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
                && salesOrderLineV2Staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId;

        if (DMFEntityExecutionParameters::getImportTaskCount(_dmfDefinitionGroupExecution.Entity, salesOrderLineV2Staging.RecId) > 1)
        {
            SalesOrderLineV2Staging::updateLineNumbers(_dmfDefinitionGroupExecution);
        }
    }

