    public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        super(_entityCtx, _dataSourceCtx);

        if (_dataSourceCtx.name() == dataEntityDataSourceStr(VendVendorEntity, DirPartyBaseEntity))
        {
            // Takes care of maintaining the reference to existing parties if this record provides a party number.
            DirPartyBaseEntity::initializeDirPartyBaseEntityDataSource(_entityCtx, _dataSourceCtx);
        }
    }

public void initValue()
{
    if (!this.skipNumberSequenceCheck())
    {
        NumberSeqRecordFieldHandler::enableNumberSequenceControlForField(
            this, fieldNum(VendVendorEntity, VendorAccountNumber), VendParameters::numRefVendAccount());
    }
            
    super();
}

    public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        boolean ret;

        if (_dataSourceCtx.name() == dataEntityDataSourceStr(VendVendorEntity, DirPartyBaseEntity))
        {
            DirPartyBaseEntityHelper partyHelper = new DirPartyBaseEntityHelper();

            // Coordinates saving between party and address entity datasources, the last parameter will let address entity create a new address for update.
            partyHelper.preUpdateEntityDataSource(_entityCtx, _dataSourceCtx,
                dataEntityDataSourceStr(VendVendorEntity, LogisticsPostalAddressBaseEntity), NoYes::No);

            ret = super(_entityCtx, _dataSourceCtx);

            if (ret)
            {
                var addressDataSource = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(VendVendorEntity, LogisticsPostalAddressBaseEntity));
                partyHelper.postUpdateEntityDataSource(_entityCtx, _dataSourceCtx, addressDataSource.name());
                this.mapAddressDataSourceToEntity(_dataSourceCtx, addressDataSource);
            }
        }
        else if (_dataSourceCtx.name() == dataEntityDataSourceStr(VendVendorEntity, VendTable))
        {
            ret = this.validateVendTableDataSource(_dataSourceCtx) && super(_entityCtx, _dataSourceCtx);
        }
        else
        {
            ret = super(_entityCtx, _dataSourceCtx);
        }

        return ret;
    }

    public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        boolean ret;

        if (_dataSourceCtx.name() == dataEntityDataSourceStr(VendVendorEntity, DirPartyBaseEntity))
        {
            DirPartyBaseEntityHelper partyHelper = new DirPartyBaseEntityHelper();
            partyHelper.preInsertEntityDataSource(_entityCtx, _dataSourceCtx,
                dataEntityDataSourceStr(VendVendorEntity, LogisticsPostalAddressBaseEntity));

            ret = super(_entityCtx, _dataSourceCtx);

            if (ret)
            {
                var addressDataSource = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(VendVendorEntity, LogisticsPostalAddressBaseEntity));
                partyHelper.postInsertEntityDataSource(_entityCtx, _dataSourceCtx, addressDataSource.name());
                this.mapAddressDataSourceToEntity(_dataSourceCtx, addressDataSource);
            }
        }
        else if (_dataSourceCtx.name() == dataEntityDataSourceStr(VendVendorEntity, VendTable))
        {
            ret = this.validateVendTableDataSource(_dataSourceCtx) && super(_entityCtx, _dataSourceCtx);
        }
        else
        {
            if (_dataSourceCtx.getBuffer().TableId == tableNum(PurchJournalAutoSummary)
                && _dataSourceCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert)
            {
                // For performance reasons, skip the insert if the table value is the
                // default value:
                PurchJournalAutoSummary buffer = _dataSourceCtx.getBuffer();

                if (buffer.AutoSummary == NoYes::No)
                {
                    _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                }
            }

            ret = super(_entityCtx, _dataSourceCtx);
        }

        return ret;
    }

    private void mapAddressDataSourceToEntity(
        DataEntityDataSourceRuntimeContext _partyDataSourceCtx,
        DataEntityDataSourceRuntimeContext _addressDataSourceCtx)
    {
        LogisticsPostalAddressBaseEntity addressEntity;

        DirPartyBaseEntity partyEntity = _partyDataSourceCtx.getBuffer();

        select addressEntity
            where addressEntity.LocationRecId == partyEntity.PrimaryAddressLocation;

        this.fromViewDataSource(_addressDataSourceCtx.id(), addressEntity);
    }

    public void persistEntity(DataEntityRuntimeContext _entityCtx)
    {
        this.skipDataSourceValidateField(fieldNum(VendVendorEntity, InvoiceVendorAccountNumber), true);

        super(_entityCtx);
    }

    private boolean validateVendTableDataSource(DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        boolean ret = true;

        if (strLen(this.InvoiceVendorAccountNumber) && this.VendorAccountNumber != this.InvoiceVendorAccountNumber)
        {
            ret = _dataSourceCtx.getBuffer().validateField(fieldNum(Vendtable, InvoiceAccount));
        }

        // updateEntityDataSource() failure does not prevent the datasource from being saved, unless it is an inner join
        // In case of update operation, throwing validation error is needed to cancel the update
        if (!ret && _dataSourceCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Update)
        {
            _dataSourceCtx.throwValidateWriteFailed();
        }

        return ret;
    }

