}

    /// <summary>
    /// Maps the entity to corresponding datasources, including applications of change proposal functionality.
    /// </summary>
    /// <param name = "_entityCtx">The entity runtime context.</param>
    /// <param name = "_dataSourceCtx">The datasource context.</param>
    public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        if (_dataSourceCtx.name() == dataEntityDataSourceStr(VendVendorEntity, VendTable))
        {
            this.processChangesForApproval(_entityCtx);
        }
        else if (_dataSourceCtx.name() == dataEntityDataSourceStr(VendVendorEntity, DirPartyBaseEntity))
        {
            this.processChangesForApproval(_entityCtx);
        }

        super(_entityCtx, _dataSourceCtx);
    }

    /// <summary>
    /// Validates the contents prior to writing it.
    /// </summary>
    /// <param name = "_entityCtx">The entity context, containing a database operation type.</param>
    private void processChangesForApproval(DataEntityRuntimeContext _entityCtx)
    {
        VendTableChangeProposalDataEntityBehavior selectedBehavior = VendParameters::find().ChangeProposalDataEntityBehavior;

        if (selectedBehavior == VendTableChangeProposalDataEntityBehavior::AllowWithoutApproval)
        {
            // Bypass all validation, since we elected not to restrict.
            return;

            // Doing this early exit for "allow without approval", which is the only entity behavior option where everything will be persisted as is,
            // allows us to assume we are dealing with a "reject" or "approval required" behavior, meaning
            // we can safely roll back the change-controlled field on the data entity if we detect a change.
        }
   
        if(_entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Update)
        {
            boolean controlledChangesMade = this.containsControlledChanges();

            if(!controlledChangesMade)
            {
                // Nothing to change, can early exit.
                // Can also assume if we don't hit this exit that a change request will be required.
                return;
            }

            if(selectedBehavior == VendTableChangeProposalDataEntityBehavior::RejectChanges)
            {
                // Making changes while settings require us to reject changes.
                throw error("@AccountsPayable:VendChangeProposal_CannotBeChangedFromDE");
            }

            VendTable vend = VendTable::find(this.VendorAccountNumber);
            RecId vendRecId = vend.RecId;

            if (!vendRecId)
            {
                // We are trying to update a record that does not exist.
                throw error(Error::wrongUseOfFunction(funcName()));
            }

            if (!vend.canModifyChangeProposal())
            {
                // Changes are made to controlled fields, but the vendor is not in a position to accept them
                // due to an existing set of pending changes.
                throw error("@AccountsPayable:VendTableChangeProposal_ChangesNotAllowed");
            }


            

            // If we got this far without early exiting or throwing, then we must apply the changes.
            VendTableChangeProposal change = VendTableChangeProposal::findOrCreateAutogenerated(vendRecId, true);

            this.initializeVendTableChangeProposal(change);

            ttsbegin;          
            change.update();
            ttscommit;
        }
        
    }

    /// <summary>
    /// Initializes the <c>VendTableChangeProposal</c> table buffer.
    /// </summary>
    /// <param name = "_vendTableChangeProposal">
    /// <c>VendTableChangeProposal</c> table buffer.
    /// </param>
    protected void initializeVendTableChangeProposal(VendTableChangeProposal _vendTableChangeProposal)
    {
        if (VendTableChangeProposalFieldEnablement::isEnabledFor(VendTableChangeProposalField::Name)
