Method.Source:
    /// <summary>
    /// Maps entity fields to the corresponding data source fields prior to record insert.
    /// </summary>
    /// <param name = "_entityCtx">The entity runtime context.</param>
    /// <param name = "_dataSourceCtx">The datasource context with datasource buffers.</param>
    public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
    {
        if (_dataSourceCtx.name() == dataEntityDataSourceStr(VendVendorParametersEntity, VendParameters))
        {
            if (this.SettlementWriteOffReasonCode && this.isFieldModified(fieldNum(VendVendorParametersEntity, SettlementWriteOffReasonCode)))
            {
                this.defaultSettlementWriteOffDimensionFromReasonCode(_dataSourceCtx.getBuffer());
            }
        }

        super(_entityCtx, _dataSourceCtx);
    }
